        var firstName = data.profile.first_name;
        var userEmail = data.profile.email;
        console.log(userEmail)

        Router.push({
            pathname: '/posts',
            query: { name: myName,
                    id: myID,
                    firstname: firstName,
                    userEmail: userEmail
