import { withRouter } from "next/router";
import Router from "next/router";
import Link from 'next/link';
import Head from 'next/head';
import { useState, useEffect } from "react";


const about = withRouter(({ router:  { query:{name, id, firstname, userEmail}}} ) => {

    // Redirect to myprofile
    const myProfile = () => {
        Router.push({
            pathname: '/myprofile',
            query: { 
                    mytitle: '',
                    author: '',
                    name: name,
                    id: id,
                    firstname: firstname,
                    userEmail:userEmail

            }
        });
    }

    // Redirect to new
    const myBooks = () => {
        Router.push({
            pathname: '/new',
            query: { 
                    id: id,
                    name: name,
                    firstname: firstname,
                    userEmail:userEmail

            }
        });
    }

    // Redirect to posts
    const Search = () => {
        Router.push({
            pathname: '/posts',
            query: { 
                    id: id,
                    name: name,
                    firstname: firstname,
                    userEmail:userEmail

            }
        });
    }


    return(
        <div className='about-page'>
            <Head>
            <title>About us</title>
        </Head>
            <div className='book-greeting' style={{ height:'auto', minHeight:'0px' }}>
            <div className='greeting-text'>
            <div className='icon-box'>
                    <Link href='/'>
                        <img src="/icons/sign-out.png" alt="my image" className='my-icon'/>
                    </Link>

                    <div className='my-icon-end'>
                    <img src="/icons/four-square.png" alt="my books" className='my-icon' onClick={myProfile}/>

                    <img src="/icons/plus.png" alt="Add book" className='my-icon' onClick={myBooks}/>
                    <img src="/icons/search.png" alt="Search" className='my-icon' onClick={Search}/>

                    </div>

                </div>




            </div>

        </div>
            <div className='aboutus-header'>About us</div> 
            <div className='about-box1'>
                <div className='about-boxb'>
                    <p>pic ture goes here</p>
                </div>
                <div className='about-boxa'>
                    <h2 className='about-title'>Our goal</h2>
                    <div className='about-text'>
                        <p>We want to make trading your university used books as 
                            easy and effortless as possible. For many students, finding 
                            used books can be a pain. With the countless university groups existing, 
                            scrolling while hoping whether the book your are looking for is somewhere in
                            there is not the convenient method. <br/>
                            This app offers the same focused search radius while providing instant results, 
                            saving you time on your already busy schedule.
                        </p>
                    </div>
                </div>

            </div>
            <div className='about-box2'>
                <div className='about-boxa'>
                <h2 className='about-title'>Our goal</h2>
                    <div className='about-text'>
                        <p>Test goes here</p>
                    </div>                </div>
                <div className='about-boxa'>
                <h2 className='about-title'>Advantages</h2>
                    <div className='about-text'>
                        <p>This app combines the focused search radius 
                            of university facebook groups and the search ability
                            of wider browsing systems to provide you with exactly what 
                            you need, finding your university books and selling your old ones. 
                        </p>
                    </div>                </div>

            </div>
            <div className='feedback'>
                <textarea ></textarea>
            </div>
        </div>
    )
})

export default about;
