import Link from 'next/link'
import Head from 'next/head'


import { FacebookProvider, LoginButton } from 'react-facebook';



var myID=null;
var myName = null;

const Index = () => {

    const handleResponse = (data) => {
        console.log(data);

        //var myID = data.id;
        //var myName = data.name
      };

     const handleError = (error) => {
        this.setState({ error });
      };

      return(
    <div className='login-page'>
    <Head>
        <title>MacEwan Book Trade</title>
    </Head>
    <div className='my-title'>Macewan Book Exchange</div>
    <div className='my-intro'>Login via facebook and start trading your macEwan books!</div>
    <FacebookProvider appId="1338252696564421">
        <LoginButton
          scope="email"
          onCompleted={handleResponse}
          onError={handleError}
          className="btn btn-primary btn-fb"
        >
          <span>Login via Facebook</span>
        </LoginButton>
      </FacebookProvider>
<Link href='/posts'>Go to posts</Link>
</div>
      )
    };



export default Index;
