import React, { useState, useEffect } from 'react';
import { FacebookProvider, LoginButton, FacebookLogin } from 'react-facebook';
import Login from 'react-facebook/dist/Login';


const searchRedirect= () => {


    //Handle facebook login data
    const handleResponse = (data) => {
        console.log(data);

        //Toggle logincheck




        var myID = data.profile.id;
        var myName = data.profile.name;
        var firstName = data.profile.first_name;
        var userEmail = data.profile.email;
        console.log(userEmail)

        Router.push({
            pathname: '/posts',
            query: { name: myName,
                    id: myID,
                    firstname: firstName,
                    userEmail: userEmail
            },
            asPath:'/search'

          },'/search')
      };

     const handleError = (error) => {
        this.setState({ error });
      };



    const responseFacebook = (response) => {
        console.log(response);
        console.log('That was the response')
      }


    useEffect(() =>{
        console.log('Will redirect');

        var LoginBtn = document.getElementById('LoginBtn')
        var prov = document.getElementById('provider')
        console.log(LoginBtn)
        console.log(prov)








    })

    return(
        <div>
        <h2>Redirecting...</h2>

            <FacebookProvider appId="2605624903036488" id='provider'>

        <LoginButton
          scope="email"
          onCompleted={handleResponse}
          onError={handleError}
          className="btn btn-primary btn-fb"
          id = 'LoginBtn'
        >
          <span><b>Login via Facebook</b></span>
        </LoginButton>

      </FacebookProvider>

      </div>
    )

}

export default searchRedirect;
