import React, { useState, useEffect } from 'react';
import { FacebookProvider, LoginButton, FacebookLogin, facebookActions } from 'react-facebook';
import Login from 'react-facebook/dist/Login';
import Router from 'next/router';
import { useRouter } from 'next/router';
import { withRouter } from "next/router";



const searchRedirect= () => {

    const dataCheck = (data) =>{
        console.log(data);
    }


    const handleResponse = (data) => {
        console.log(data);

        //Toggle logincheck




        var myID = data.profile.id;
        var myName = data.profile.name;
        var firstName = data.profile.first_name;
        var userEmail = data.profile.email;
        console.log(userEmail)


        Router.push({
            pathname: '/posts',
            query: { name: myName,
                    id: myID,
                    firstname: firstName,
                    userEmail: userEmail
            },
            asPath:'/search'

          },'/search')
      };


     const handleError = (error) => {
        this.setState({ error });
      };







    useEffect(() =>{
        console.log('Redirecting');
        console.log(document.getElementById('mybtn'))

    })

    return(
        <div className='redirect-page'>
        <h2>Redirecting...</h2>

        <FacebookProvider appId="2605624903036488">

            <button id='mybtn' onClick={dataCheck}>Click here</button>

    <LoginButton
      scope="email"
      onCompleted={handleResponse}
      onError={handleError}
      className="btn btn-primary btn-fb"
    >
      <span><b>Refresh</b></span>
    </LoginButton>

  </FacebookProvider>

      </div>
    )


}

export default searchRedirect;
