import { withRouter } from "next/router";
import Link from 'next/link';

import React, { useState } from 'react';
import fetch from 'isomorphic-unfetch';

const posts = withRouter(({ router:  { query:{name, id, firstName}}, books} ) => {


    return(
    <div className='option-page'>

        <div className='book-greeting'>
            <div className='greeting-text'>
                <h2>Hi, {firstName}!</h2>

                <p>Find your book by entering 
                    the <b>Author</b> or <b>Book name</b> below.
                </p>
            </div>
                
            <form className='my-form'>
                <div class="form-group my-group">
                <label>Title</label>
                <input type="text" className="form-control my-form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter book title"/>
                </div>
                <div>OR</div>
                <div class="form-group my-group">
                <label>Author</label>
                <input type="text" className="form-control" id="exampleInputPassword1" placeholder="Enter Author"/>
                
  
                <button type="submit" class="btn btn-primary my-btn">Search</button>
                </div>
                </form>

                <div className='my-divider'></div>
                <div className='btn btn-success my-btn-viewall'>View all</div>
  
            
        </div>


        
    </div>

    )
    
});
  



    
export default posts;
