import { withRouter } from "next/router";
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';


const posts = withRouter(({ router:  { query:{name, id}}, entries, page, pageCount } ) => (
    <div>
        <p>Hello there, {name}</p>
        <p>Your ID is: {id}</p>

        <ul>
        {entries.map(p => (
          <li className="profile" key={p.id}>
            <Link href={`/profile?id=${p.id}`}>
              <a>
                <span>{p.userName}</span>
              </a>
            </Link>
          </li>
        ))}
      </ul>
      <nav>
        {page > 1 && (
          <Link href={`/?page=${page - 1}&limit=9`}>
            <a>Previous</a>
          </Link>
        )}
        {page < pageCount && (
          <Link href={`/?page=${page + 1}&limit=9`}>
            <a className="next">Next</a>
          </Link>
        )}
      </nav>

        
    </div>
    ));

    posts.getInitialProps = async ({ req, query }) => {
        const protocol = req
          ? `${req.headers['x-forwarded-proto']}:`
          : location.protocol
        const host = req ? req.headers['x-forwarded-host'] : location.host
        const pageRequest = `${protocol}//${host}/api/profiles?page=${query.page ||
          1}&limit=${query.limit || 9}`
        const res = await fetch(pageRequest)
        const json = await res.json()
        return json
      }
    
    
    export default posts;
