import Link from 'next/link';
import { useState, useEffect } from 'react';
import fetch from 'isomorphic-unfetch';
import { useRouter, withRouter } from 'next/router';
import Router from 'next/router';

import { Form } from 'react-bootstrap';
import { Label } from 'semantic-ui-react';

//Image resize
import Resizer from 'react-image-file-resizer';

const fileUpload = require('fuctbase64');

const EditBook = withRouter(({ router:  { query:{name, id, firstname, bookID, bookTitle, bookAuthor}}} ) => {
    const [form, setForm] = useState({ title:'', author:'', ownerID:id, ownerName:name, imageFront:{data:'/static/default-image.svg', contentType:'image/png'}}
    );
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [errors, setErrors] = useState({});

    //state of the preview image
    const [prevImage, setPrevImage] = useState('/static/default-image.svg');

    const router = useRouter();




    useEffect(() => {
        // Set form data to that from Router
        setForm({
            ...form,
                title: bookTitle,
                author: bookAuthor,
                ownerID: id,
                ownerName: name
        })

        console.log(form);
        console.log('name is '+ name)






        if (isSubmitting){
            if (Object.keys(errors).length ===0){
                //bookmatch();

                //alert('New book created')


                createBook();
                /*
                Router.push({
                    pathname: '/booklist',
                    query: { 
                            mytitle: form.title,
                            author: form.author

                    }
                });*/

            }
            else{
                setIsSubmitting(false);
            }
        }
    }, [errors])


    //Create new book post
    const createBook = async () => {

        setForm({
            ...form,
                title: bookTitle,
                author: bookAuthor,
                 ownerID: id,
                 ownerName: name,
                 imageFront:''
        })


        try {

            // Use default image as book image if no image has been given
            /*
            if (form.imageFront==''){
                console.log('default image is given')
                console.log(prevImage)
                setForm({
                    ...form,
                 imageFront:{
                     data: prevImage, 
                     contentType: 'image/png'
                 }

            })
            }
            */

            console.log(form.imageFront.data);
            console.log(form)

            //const res = await fetch('https://unibooktrade.vercel.app/api/books', {
             const res = await fetch('http://localhost:3000/api/books', {


                method: 'PUT',

                headers: {
                    "Accept": 'application/json',
                    "Content-Type": "application/json"
                },
                //body: JSON.stringify(form)
                body: JSON.stringify({
                    title: form.title,
                    author: form.author,
                    id: bookID,
                    ownerID: id,
                    ownerName: name,
                    imageFront: form.imageFront,
                })

            })




            //Redirect to profile
            Router.push({
                pathname: '/myprofile',
                query: { 
                        id: id,
                        name: name,
                        firstname: firstname,
                        mytitle: '',
                        author: ''

                }
            });


        } catch (error) {
            console.log(error)

        }
    }

    const handleSubmit = (e) => { 
        e.preventDefault();


        /*
        setForm({
            ...form,
                 ownerID: id,
                 ownerName: name,


        })
        */

        let errs = validate();
        setErrors(errs);
        setIsSubmitting(true);


    }
    const handleChange = (e) => { 

        setForm({
            ...form,
                [e.target.name]: e.target.value

        })
    }

    const validate = () => {
        let err = {};


        return err;
    }

        //Handle page switch for header icons
        const myProfile = () => {
            Router.push({
                pathname: '/myprofile',
                query: { 
                        mytitle: '',
                        author: '',
                        name: name,
                        id: id,
                        firstname: firstname

                }
            });
        }

        //Go to section to create new listing
        const myBooks = () => {
            Router.push({
                pathname: '/new',
                query: { 
                        id: id,
                        name: name,
                        firstname: firstname

                }
            });
        }

        const Search = () => {
            Router.push({
                pathname: '/posts',
                query: { 
                        id: id,
                        name: name,
                        firstname: firstname

                }
            });
        }

        //Handle image upload
        const image1Upload = (e) => {


       var fileInput = false;
       if (e.target.files[0]){
           fileInput = true;
       }
       if (fileInput) {
           Resizer.imageFileResizer(
            event.target.files[0],
            300,
            300,
            'png',
            100,
            0,
            uri => {
                console.log('im inside')

                //Update form with new image data
                setForm({
                    ...form,
                 imageFront:{
                     data: uri, 
                     contentType: 'image/png'
                 }

            })



            console.log(form)

            //Update image preview
            setPrevImage(uri);

            },
            'base64'

           );
       }
       /*
       else{
           setForm({
               imageFront:{
                   data: form.imageFront,
                   contentType: 'image/png'
               }
           })
       }
       */


        }




    return (
        <div className='newBook-page'>
            <div className='book-greeting'>
            <div className='greeting-text'>
            <div className='icon-box'>
                    <Link href='/'>
                        <img src="/icons/sign-out.png" alt="my image" className='my-icon'/>
                    </Link>

                    <div className='my-icon-end'>
                    <img src="/icons/four-square.png" alt="my books" className='my-icon' onClick={myProfile}/>

                    <img src="/icons/plus.png" alt="Add book" className='my-icon' onClick={myBooks}/>

                    <img src="/icons/search.png" alt="Search" className='my-icon' onClick={Search}/>
                    </div>




            </div>



           <div style={{ marginTop:'65px', fontSize:'18px', textAlign:'center', width:'100%' }}><p>Update your book posting</p></div>



            </div>

        </div>

        <form className='create-book-form' onSubmit={handleSubmit} style={{ marginTop:'3%' }}>
        <img src={prevImage} alt='default-image' className='image-preview'/>
        <div className="form-group my-group" style={{marginTop:'8%'}}>
                <input 
                    type="text" 
                    className="form-control new-book-input" 
                    id="exampleInputEmail1" 
                    placeholder="Enter book title"
                    name="title"
                    required
                    onChange={handleChange}/>

                </div>
                <div className="form-group my-group">
                <input 
                    type="text" 
                    className="form-control new-book-input" 
                    placeholder="Enter Author"
                    name='author'
                    onChange={handleChange}
                    required
                    />

                <label className='input-file-btn'>
                <input
                type="file"
                name="file"
                id="input-files"
                className='input-file-btn'
                onChange={image1Upload}
              />
              </label>

              <label htmlFor="file-upload" className="input-file-btn" >
            </label>


                <button type="submit" className="btn btn-success my-btn">Update</button>
                </div>
                </form>

        </div>
    )
});




export default EditBook;
