import Link from 'next/link';
import { useState, useEffect } from 'react';
import fetch from 'isomorphic-unfetch';
import { useRouter } from 'next/router';


const NewBook = () => {
    const [form, setForm] = useState({ title:'', description:''}
    );
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [errors, setErrors] = useState({});
    const router = useRouter();


    useEffect(() => {
        if (isSubmitting){
            if (Object.keys(errors).length ===0){
                //bookmatch();

                alert('New book created')
                /*
                Router.push({
                    pathname: '/booklist',
                    query: { 
                            mytitle: form.title,
                            author: form.author
                            
                    }
                });*/
              
            }
            else{
                setIsSubmitting(false);
            }
        }
    }, [errors])

    const handleSubmit = (e) => { 
        e.preventDefault();
        let errs = validate();
        setErrors(errs);
        setIsSubmitting(true);


    }
    const handleChange = (e) => { 
        setForm({
            ...form,
                [e.target.name]: e.target.value
            
        })
    }

    const validate = () => {
        let err = {};

        
        return err;
    }

    return (
        <div className='newBook-page'>
            <div className='book-greeting'>
            <div className='greeting-text'>
                <p>Find your book by entering 
                    the <b>Author</b> or <b>Book name
                        below.
                    </b>
                </p>


                
            </div>
                
        </div>
           
        <form className='create-book-form' >
        <div className="form-group my-group">
                <label>Title</label>
                <input 
                    type="text" 
                    className="form-control my-form-control" 
                    id="exampleInputEmail1" 
                    placeholder="Enter book title"
                    name="title"
                    onChange={handleChange}/>
                    
                </div>
                <b>OR</b>
                <div className="form-group my-group">
                <label>Author</label>
                <input 
                    type="text" 
                    className="form-control " 
                    placeholder="Enter Author"
                    name='author'
                    onChange={handleChange}
                    />
                
  
                <button type="submit" className="btn btn-primary my-btn">Search</button>
                </div>
                </form>
            
        </div>
    )
}

export default NewBook;
